import React, { useState } from 'react';
import './App.css';
import firebase from 'firebase/compat/app'; // Importa el módulo de autenticación de Firebase
import 'firebase/compat/auth'; // Importa el módulo de autenticación de Firebase
import Login from './Login';
import Pets from './Pets';
import ShoppingCart from './ShoppingCart';
import FondoHuellas from '../images/1.jpg'; // Importa la imagen

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyA59qbASepDAJRrIZL1yePlQ4JPe-LYhHI",
  authDomain: "huellitas-2446e.firebaseapp.com",
  projectId: "huellitas-2446e",
  storageBucket: "huellitas-2446e.appspot.com",
  messagingSenderId: "908845715064",
  appId: "1:908845715064:web:95fa77c6c85e9aaab4278d",
  measurementId: "G-TMRY8XGJ49"
};

// Initialize Firebase
const app = firebase.initializeApp(firebaseConfig);
  
function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [cart, setCart] = useState([]);

  const handleLogin = () => {
    console.log("Handle login called"); // Añade este console.log
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  // Función para iniciar sesión con Google
  const handleGoogleLogin = async () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    try {
      await firebase.auth().signInWithPopup(provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
    }
  };

  // Función para iniciar sesión con Facebook
  const handleFacebookLogin = async () => {
    const provider = new firebase.auth.FacebookAuthProvider();
    try {
      await firebase.auth().signInWithPopup(provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Facebook:', error);
    }
  };

  console.log("Logged in state:", loggedIn); // Añade este console.log

  return (
    <div className="App">
      <div className="header" style={{ backgroundImage: `url(${FondoHuellas})` }}>
        <h2>Bienvenido a nuestra tienda de mascotas</h2>
        <h1>Bienvenido a nuestra tienda de mascotas</h1>
        {loggedIn && <button onClick={handleLogout}>Cerrar Sesión</button>}
      </div>
      <div className="main-content">
        <Pets addToCart={addToCart} />
        <ShoppingCart cart={cart} />
      </div>
      {!loggedIn && <Login onLogin={handleLogin} onGoogleLogin={handleGoogleLogin} onFacebookLogin={handleFacebookLogin} />}
    </div>
  );
}

export default App;
