import React, { useState } from 'react';
import './App.css';
import firebase from 'firebase/app';
import 'firebase/auth';
import Login from './Login';
import Pets from './Pets';
import ShoppingCart from './ShoppingCart';
import FondoHuellas from '../images/fondohuellas2.jpg';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [cart, setCart] = useState([]);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  return (
    <div className="App">
      <div className="header" style={{ backgroundImage: `url(${FondoHuellas})` }}>
        <h1>Bienvenido a nuestra tienda de mascotas</h1>
        {loggedIn && <button onClick={handleLogout}>Cerrar Sesi√≥n</button>}
      </div>
      <div className="main-content">
        <Pets addToCart={addToCart} />
        <ShoppingCart cart={cart} />
      </div>
      {!loggedIn && <Login onLogin={handleLogin} />}
    </div>
  );
}

export default App;
