import React, { useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import './App.css';
import firebase from 'firebase/app'; 
import 'firebase/auth'; // Importa los módulos de autenticación
import Login from './Login';

const firebaseConfig = {
  apiKey: "AIzaSyA59qbASepDAJRrIZL1yePlQ4JPe-LYhHI",
  authDomain: "4 huellitas.firebaseapp.com",
  projectId: "huellitas-2446e",
  storageBucket: "TU_STORAGE_BUCKET",
  messagingSenderId: "908845715064",
  appId: "1:908845715064:web:95fa77c6c85e9aaab4278d",
  measurementId: "TU_MEASUREMENT_ID"
};

// Inicializa la app de Firebase
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    console.log("Handle login called");
    setLoggedIn(true);
  };

  const handleGoogleLogin = async () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    try {
      await firebase.auth().signInWithPopup(provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
    }
  };

  const handleFacebookLogin = async () => {
    const provider = new firebase.auth.FacebookAuthProvider();
    try {
      await firebase.auth().signInWithPopup(provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Facebook:', error);
    }
  };

  const handleCreateAccount = () => {
    // Lógica para redirigir a la página de registro o ejecutar la creación de cuenta
    console.log("Create account clicked");
  };

  console.log("Logged in state:", loggedIn);

  return (
    <Router>
      <div className="App">
        {!loggedIn && <Login onLogin={handleLogin} onGoogleLogin={handleGoogleLogin} onFacebookLogin={handleFacebookLogin} onCreateAccount={handleCreateAccount} />}
        {/* Aquí puedes agregar más rutas para otras páginas de tu aplicación */}
      </div>
    </Router>
  );
}

export default App;
