import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import './Login.css'; // Importa los estilos para el inicio de sesión

// Configuración de Firebase
const firebaseConfig = {
  apiKey: "TU_API_KEY",
  authDomain: "TU_DOMINIO.firebaseapp.com",
  projectId: "TU_ID_DE_PROYECTO",
  storageBucket: "TU_STORAGE_BUCKET",
  messagingSenderId: "TU_SENDER_ID",
  appId: "TU_APP_ID",
  measurementId: "TU_MEASUREMENT_ID"
};

// Inicializamos Firebase
const app = firebase.initializeApp(firebaseConfig);

function Login({ onLogin, onGoogleLogin, onFacebookLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica para autenticar al usuario con nombre de usuario y contraseña
    // Aquí solo llamamos a la función onLogin para simular el inicio de sesión
    onLogin();
  };

  return (
    <div className="login-box">
      <h2>Iniciar Sesión</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nombre de usuario"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Iniciar Sesión</button>
      </form>
      <div className="social-login">
        <h2>O inicia sesión con:</h2>
        <button onClick={onGoogleLogin}>Google</button>
        <button onClick={onFacebookLogin}>Facebook</button>
      </div>
    </div>
  );
}

export default Login;
