import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Redirect } from 'react-router-dom';
import './App.css';
import { initializeApp } from 'firebase/app'; 
import { getAuth, GoogleAuthProvider, FacebookAuthProvider, signInWithPopup } from 'firebase/auth';
import Login from './Login';
import Tienda from './Tienda';
import ShoppingCart from './ShoppingCart';
import HomeCookedMeals from './HomeCookedMeals';
import PetWeddings from './PetWeddings';
import Healthcare from './Healthcare';
import PetNames from './PetNames';

// Configuración de Firebase
const firebaseConfig = {
  apiKey: "AIzaSyA59qbASepDAJRrIZL1yePlQ4JPe-LYhHI",
  authDomain: "4-huellitas.firebaseapp.com",
  projectId: "huellitas-2446e",
  storageBucket: "huellitas-2446e.appspot.com",
  messagingSenderId: "908845715064",
  appId: "1:908845715064:web:95fa77c6c85e9aaab4278d",
  measurementId: "G-TMRY8XGJ49"
};

// Inicialización de Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
    }
  };

  const handleFacebookLogin = async () => {
    const provider = new FacebookAuthProvider();
    try {
      await signInWithPopup(auth, provider);
      setLoggedIn(true);
    } catch (error) {
      console.error('Error al iniciar sesión con Facebook:', error);
    }
  };

  const handleCreateAccount = () => {
    // Lógica para redirigir a la página de registro o ejecutar la creación de cuenta
  };

  if (loggedIn) {
    return <Redirect to="/tienda" />;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Login onLogin={handleLogin} onGoogleLogin={handleGoogleLogin} onFacebookLogin={handleFacebookLogin} onCreateAccount={handleCreateAccount} />} />
          <Route path="/tienda" element={<Tienda />} />
          {/* Agrega más rutas para otras páginas de tu aplicación */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
